import React, { useEffect, useState } from "react";
import axios from "axios";
import "./message.css";

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");

  // ✅ Backend ka base URL
  const API_URL = "http://localhost:5000/messages";

  // ✅ Messages fetch karne ka function
  const fetchMessages = async () => {
    try {
      const res = await axios.get(API_URL);
      setMessages(res.data);
    } catch (error) {
      console.error("Error fetching messages", error);
    }
  };

  // ✅ Message send karne ka function
  const sendMessage = async () => {
    if (!input.trim()) return;

    const newMsg = {
      wa_id: "919876543210",
      name: "Ridhi",
      text: input,
      fromMe: true,
      timestamp: new Date(),
      status: "sent"
    };

    try {
      await axios.post(API_URL, newMsg);
      setInput("");
      fetchMessages(); // Turant refresh
    } catch (error) {
      console.error("Error sending message", error);
    }
  };

  // ✅ Page load pe fetch
  useEffect(() => {
    fetchMessages();
  }, []);

  return (
    <div className="app">
      <div className="chat-header">WhatsApp Clone</div>

      <div className="chat-body">
        {messages.map((msg, index) => (
          <div
            key={index}
            className={`message ${msg.fromMe ? "sent" : "received"}`}
          >
            <span className="name">{msg.name}</span>
            <p>{msg.text}</p>
            <span className="time">
              {new Date(msg.timestamp).toLocaleTimeString()}
            </span>
          </div>
        ))}
      </div>

      <div className="chat-footer">
        <input
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Type a message"
          onKeyDown={(e) => e.key === "Enter" && sendMessage()}
        />
        <button onClick={sendMessage}>Send</button>
      </div>
    </div>
  );
}

export default App;
