import React, { useState, useEffect } from "react";
import "./App.css";
import whatsapp from './assets/whatsapp.png';
import neww from './assets/new.png'
import filter from './assets/filter.png';
import './Navbar.css';
import Navbar from "./Navbar";

import tick from './assets/tick.png'
import tickk from './assets/check.png'
import axios from "axios";
import "./message.css";

const App = () => {
  const [messages, setMessages] = useState([]);
    const [input, setInput] = useState("");

    // âœ… Backend ka base URL
    const API_URL = "http://localhost:5000/messages";

    // âœ… Messages fetch karne ka function
    const fetchMessages = async () => {
      try {
        const res = await axios.get(API_URL);
        setMessages(res.data);
      } catch (error) {
        console.error("Error fetching messages", error);
      }
    };

    // âœ… Message send karne ka function
    const sendMessage = async () => {
      if (!input.trim()) return;

      const newMsg = {
        wa_id: "919876543210",
        name: "Ridhi",
        text: input,
        fromMe: true,
        timestamp: new Date(),
        status: "sent"
      };

      try {
        await axios.post(API_URL, newMsg);
        setInput("");
        fetchMessages(); // Turant refresh
      } catch (error) {
        console.error("Error sending message", error);
      }
    };

    // âœ… Page load pe fetch
    useEffect(() => {
      fetchMessages();
    }, []);
  const [showSplash, setShowSplash] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => setShowSplash(false), 1500);
    return () => clearTimeout(timer);
  }, []);

  return (
    <div className="app-container">
      {showSplash ? (
        <div className="splash-screen">
          <img src={whatsapp} alt="WhatsApp Logo" />
        </div>
      ) : (
        <>
          <div className="up">
            <img src={whatsapp} className="uplogo" alt="WhatsApp Logo" />
            <h3>WhatsApp</h3>
          </div>
            <Navbar/>

            <div className="main-ui">
              <div className="sidebar">
                <div className="sidebar-header">
                  <div>
                    <h2>Chats</h2>
                  </div>
                  <div className="icons">
                    <img src={neww}/>
                    <img src={filter}/>
                  </div>
                </div>
                <input
                  className="search-box"
                  placeholder="Search and start a new chat">
                  </input>

                <div className="chat-list">
                  <div className="chat-item">
                    <div className="chat-avatar">R</div>
                    <div className="chat-info">
                      <div className="chat-name">
                        <div className="name">RidhiðŸ’—
                         </div>
                         <div className="clock">
                          <div>
                          <p >03:45 PM</p> 
                          </div>
                          {/* <div className="msg">
                            <p className="para">3</p>
                          </div> */}
                        </div>
                      </div>
                      <div className="chat-last">
                        <p>Let's meet todayðŸ˜€</p>
                       <p className="para">3</p>
                      </div>
                    </div>
                  </div>
                  <div className="chat-item">

                    <div className="chat-avatar1">N</div>
                    <div className="chat-info">
                      <div className="chat-name">
                        <div className="name">Neha
                         </div>
                         <div className="time">
                          <p>11:05 AM</p> 

                        </div>
                      </div>
                      <div className="chat-last">
                        <img src={tick} className="tick"/>
                        <p>I'm good. What about you?</p></div>
                    </div>
                  </div>
                    <div className="chat-item">
                    <div className="chat-avatar2">B</div>
                    <div className="chat-info">
                      <div className="chat-name">
                        <div className="name">Bhavna Sharma
                          </div>
                         <div className="time">
                          <p>07:54 AM </p> 
                        </div>
                          </div>
                      <div className="chat-last">
                        <img src={tickk} className="tick1"/>
                      <p>where are you?</p>
                      </div>
                    </div>
                  </div>
                    <div className="chat-item">
                    <div className="chat-avatar3">C</div>
                    <div className="chat-info">
                      <div className="chat-name">
                        <div className="name">
                         Chandan
                        </div>
                        <div className="time">
                          <p>Yesterday</p> 
                        </div>
                      </div>
                      <div className="chat-last">
                        <img src={tick} className="tick"/>
                        <p>Hey! What's up??</p></div>
                    </div>
                  </div>
                </div>
              </div>

              <div className="chat-area">


               <div className="app">
      <div className="chat-header">WhatsApp Clone</div>

      <div className="chat-body">
        {messages.map((msg, index) => (
          <div
            key={index}
            className={`message ${msg.fromMe ? "sent" : "received"}`}
          >
            <span className="name">{msg.name}</span>
            <p>{msg.text}</p>
            <span className="time">
              {new Date(msg.timestamp).toLocaleTimeString()}
            </span>
          </div>
        ))}
      </div>

      <div className="chat-footer">
        <input
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Type a message"
          onKeyDown={(e) => e.key === "Enter" && sendMessage()}
        />
        <button onClick={sendMessage}>Send</button>
      </div>
    </div>

              </div>
            </div>
        </>
      )}
    </div>
  );
};

export default App;
